const wm = this.getWrexMods();
const fs = require("fs");
const nodegyp = require('node-gyp');
const db = wm.require('quick.db');
const path = `./resources/database/${server.id}/log_chan.txt`;
const emojicreated = tempVars('emojicreated');
let embed = new DiscordJS.RichEmbed();

if (fs.existsSync(path)) {
    const log_chan = fs.readFileSync(path, 'utf8');
    const log_system = db.get(`${server.id}log_system.emoji-created`);
    if (log_system === "enabled") {
        embed.setTitle('Emoji Created');
        embed.setColor('#20fc3a');
        embed.setThumbnail(emojicreated.url);
        embed.addField('Name', emojicreated.name, true);
        embed.addField('Identifier', emojicreated.identifier, true);
        embed.addField('Is Animated?', emojicreated.animated, true);
        embed.setFooter(`ID: ${emojicreated.id}`);
        embed.setTimestamp();
        server.channels.get(log_chan).send(embed);
        db.add(`${server.id}logs.emoji_created`, 1);
        db.add(`${server.id}logs.all`, 1);
    }
}
